import React from 'react'
import {NavLink} from 'react-router-dom'
import {card, name, button, weatherImage, details} from './City.module.css'

function City({city}) {
  if ( 22 < city.nameBackUp.length && city.nameBackUp.length < 28) {
    city.name = city.nameBackUp
  }

  else {city.name = city.nameBackUp.slice(0,28)}

  if (city !== undefined) {
    console.log(city)
    return (
      <div className={card}>
        <h2 className={name}>{city.name}</h2>

        <NavLink to='/'>
          <button className={button}> X </button>  
        </NavLink>

        <img src={`http://openweathermap.org/img/wn/${city.img}@2x.png`}
          alt="Weather"
          className={weatherImage}/>

        <ul className={details}>
          <li> Temperature:  {Math.round(city.temperature)}Â°C </li>
          <li> Weather: {city.weather} </li>
          <li> Wind: {city.wind} </li>
          <li> Amount of clouds: {city.clouds} </li>
          <li> Latitude: {city.latitude} </li>
          <li> Longitude: {city.longitude} </li>
        </ul>
      </div>
    )
  } 

  else {return (
    <div style={{color: 'white', fontSize: '20pt', textAlign: 'left', margin: '20px'}}>
      404 ERROR: CITY NOT FOUND.
    </div>  
  )}
}

export default City;
