// Cards component stores each weather card.
// Nav component manages navigation bar.
import React, {useState} from 'react';
import {Route} from 'react-router-dom';
import './App.css';
import Cards from './components/Cards.jsx';
import Nav from './components/Nav.jsx'
import About from './components/About'
import City from './components/City';

function App() {

  const [cities, setCities] = useState([])

  function searchCity(cityName) {
    const apiKey='4ae2636d8dfbdc3044bede63951a019b'

    fetch(`http://api.openweathermap.org/data/2.5/weather?q=${cityName}&appid=${apiKey}&units=metric`)
      .then(data => data.json())
      .then((cityData) => {
        if (cityData.main !== undefined){
          const city = {
            name: cityData.name,
            min: cityData.main.temp_min,
            max: cityData.main.temp_max,
            img: cityData.weather[0].icon,
            id: cityData.id,
            wind: cityData.wind.speed,
            temperature: cityData.main.temp,
            weather: cityData.weather[0].main,
            clouds: cityData.clouds.all,
            latitude: cityData.coord.lat,
            longitude: cityData.coord.lon
          }

          setCities([...cities,city]);

        } else {
          alert("Ciudad no encontrada");
        }
      });
  }

  function onClose(id) {
    setCities(cities.filter(city => city.id !== id))
  }

  return (
    <div className='App'>
      <Route path='/'>
        <Nav searchCity={searchCity}/>
      </Route>
      <Route exact path='/'>
        <Cards cities={cities} onClose={onClose}/>
      </Route>
      <Route path='/about'>
        <About/>
      </Route>
      <Route 
        exact path='/city/:id'
        render={({match}) => <City
          city={cities.filter(city => city.id === Number(match.params.id))[0]}
        />}
      />
    </div>
  );
}

export default App;
