// Cards component stores each weather card.
// Nav component manages navigation bar.
import React, {useState} from 'react';
import {Route} from 'react-router-dom';
import './App.css';
import Cards from './components/Cards.jsx';
import Nav from './components/Nav.jsx'
import About from './components/About'
import City from './components/City';

function App() {

  const [cities, setCities] = useState([])

  function searchCity(city) {
    const apiKey='4ae2636d8dfbdc3044bede63951a019b'

    fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`)
      .then(data => data.json())
      .then((cityData) => {
        if (cities.includes(cityData)) {alert('hola')}

        else if (cityData.main !== undefined){
          setCities([...cities,cityData]);

        } else {
          alert("Ciudad no encontrada");
        }
      });
  }

  function onClose(name) {
    setCities(cities.filter(city => city.name !== name))
  }

  return (
    <div>
      <Route exact path='/' className='App'>
        <Nav searchCity={searchCity}/>
        <Cards cities={cities} onClose={onClose}/>
      </Route>
      <Route path='/about' component={About}/>
      <Route path='/city' component={City}/>
    </div>
  );
}

export default App;
