// Cards component stores each weather card.
// Nav component manages navigation bar.
import React, {useState} from 'react';
import './App.css';
import Cards from './components/Cards.jsx';
import Nav from './components/Nav.jsx'
import data, {Cairns} from './data.js';

function App() {

  const [cities, setCities] = useState([])

  function searchCity(city) {
    const exampleCity = {
      min: 32,
      max: 35,
      img: "03n",
      id: 2172797,
      wind: 3.6,
      temp: 300.15,
      name: "Cairns",
      weather: "Clouds",
      clouds: 40,
      latitud: -16.92,
      longitud: 145.77
    }

    setCities([...cities,exampleCity])
    // setCities(oldCities => [...oldCities, exampleCity]);
  }

  function onClose(id) {
      setCities(oldCities => oldCities.filter(c => c.id != id));
  }

  return (
    <div className="App">
      <Nav searchCity={searchCity}/>
      <Cards cities={cities} onClose={onClose}/>
    </div>
  );
}

export default App;

