import React, {useState} from 'react';
import {form, input, button} from './SearchBar.module.css'

export default function SearchBar(props) {
  // Ac치 va tu c칩digo
  const [city, addCity] = useState('')
  return ( 
    <form className={ form }>
      <input className={ input } type="text" placeholder="Ciudad"
        onChange = { (event) => {
          addCity(event.target.value)
        } }
      />

      <button className={ button } 
        onClick = { (event) => {
          event.preventDefault();
          props.onSearch(city)
        } }
      >
        Agregar
      </button>
    </form>
  )
};
