import React from 'react';
import {NavLink} from 'react-router-dom';
import {card, button, city, container, temperature, img} from './Card.module.css'

function Card({onClose, city}) {
  // Acá va tu código
  return (
    <div className={card}>
      <button className={button} onClick={onClose}>X</button>

      <NavLink to={`/city/${city.id}`} style={{textDecoration:'none'}}>
        <h1 className={cityClass}> {city.name} </h1>
      </NavLink>
      <div className={container}>
        <ul className={temperature}>
          <li> Máx.</li> 
          <li>{Math.round(city.max)+'°'}</li>
        </ul> 

        <ul className={temperature}>
          <li> Mín.</li> 
          <li>{Math.round(city.min)+'°'}</li>
        </ul> 

        <img src={`http://openweathermap.org/img/wn/${city.img}@2x.png`}
          alt="Weather"
          className={img}/>
      </div>
    </div>
  )
};

export default Card;
