import React from 'react';
import {NavLink} from 'react-router-dom';
import {card, button, name, container, temperature, img} from './Card.module.css'

function Card({onClose, city}) {
  // Acá va tu código
  // Reduce city.name length if it is too long to fit the card.
  if (20 < city.name.length) {
    city.name = city.name.slice(0,20);
    city.name += '...';
  }
  return (
    <div className={card}>
      <button className={button} onClick={onClose}>X</button>

      <NavLink to={`/city/${city.id}`} activeStyle={{textDecoration:'none'}}>
        <h1 className={name}> {city.name} </h1>
      </NavLink>
      <div className={container}>
        <ul className={temperature}>
          <li> Max.</li> 
          <li>{Math.round(city.max)+'°C'}</li>
        </ul> 

        <ul className={temperature}>
          <li> Min.</li> 
          <li>{Math.round(city.min)+'°C'}</li>
        </ul> 

        <img src={`http://openweathermap.org/img/wn/${city.img}@2x.png`}
          alt="Weather"
          className={img}/>
      </div>
    </div>
  )
};

export default Card;
