import React from 'react'
import {NavLink} from 'react-router-dom'
import {card, name, button, weatherImage, details} from './City.module.css'

function City({city}) {
  if (city !== undefined) {
    console.log(city)
    return (
      <div className={card}>
        <h2 className={name}>{city.name}</h2>

        <NavLink to='/'>
          <button className={button}> X </button>  
        </NavLink>

        <img src={`http://openweathermap.org/img/wn/${city.img}@2x.png`}
          alt="Weather"
          className={weatherImage}/>

        <ul className={details}>
          <li> Temperatura:  {Math.round(city.temperature)}Â°C </li>
          <li> Clima:  {city.weather} </li>
          <li> Viento:  {city.wind} </li>
          <li> Cantidad de nubes:  {city.clouds} </li>
          <li> Latitud:  {city.latitude} </li>
          <li> Longitud: {city.longitude} </li>
        </ul>
      </div>
    )
  } else {return (
    <div style={{color: 'white', fontSize: '20pt', textAlign: 'left', margin: '20px'}}>
      Error 404: ciudad no encontrada
    </div>  
  )}
}

export default City;
