import React, {useState} from 'react';
import {form, input, button} from './SearchBar.module.css'

function SearchBar(props) {

  // Ac치 va tu c칩digo

  const [city, inputCity] = useState('')

  return ( 
    <form className={form}>
      <input className={input} type="text" placeholder="Ciudad"
        value={city}
        onChange={(event) => {
          inputCity(event.target.value)
        }}
      />

      <button className={button} 
        onClick={(event) => {
          event.preventDefault();
          if (city !== '') props.searchCity(city)
          inputCity('')
        }}
      >
        Agregar
      </button>
    </form>
  )
};

export default SearchBar;
