// Cards component stores each weather card.
// Nav component manages navigation bar.
import React, {useState} from 'react';
import './App.css';
import Cards from './components/Cards.jsx';
import Nav from './components/Nav.jsx'

function App() {

  const [cities, setCities] = useState([])

  function searchCity(city) {
    const apiKey='4ae2636d8dfbdc3044bede63951a019b'

    fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`)
      .then(data => data.json())
      .then((cityData) => {
        if (cities.includes(cityData)) {}

        else if (cityData.main !== undefined){
          setCities([...cities,cityData]);
          // setCities(oldCities => [...oldCities, city]);

        } else {
          alert("Ciudad no encontrada");
        }
      });
  }

  function onClose(id) {
    // setCities(cities.filter(city => city.weather[0].id !== id))
    setCities(oldCities => oldCities.filter(city => city.weather[0].id !== id));
  }

  return (
    <div className="App">
      <Nav searchCity={searchCity}/>
      <Cards cities={cities} onClose={onClose}/>
    </div>
  );
}

export default App;

