import React from 'react';
import {NavLink} from 'react-router-dom';
import {card, button, city, container, temperature, img} from './Card.module.css'

function Card(props) {
  // Acá va tu código
  return (
      <NavLink to={`/city/${props.id}`}>
    <div className={card}>
      <button className={button} onClick={props.onClose}>X</button>

        <h1 className={city}> {props.name} </h1>
      <div className={container}>
        <ul className={temperature}>
          <li> Máx.</li> 
          <li>{Math.round(props.max)+'°'}</li>
        </ul> 

        <ul className={temperature}>
          <li> Mín.</li> 
          <li>{Math.round(props.min)+'°'}</li>
        </ul> 

        <img src={`http://openweathermap.org/img/wn/${props.img}@2x.png`}
          alt="Weather"
          className={img}/>
      </div>
    </div>
      </NavLink>
  )
};

export default Card;
