import React from 'react';
import Card from './Card.jsx'
import {citiesStyle, cityStyle} from './Cards.module.css'

function Cards(props) {
  // Acá va tu código
  // Sugerencia: podés usar el método map
  const cities = props.cities.map((city,index) =>
    <li className={cityStyle} key={index}> 
      <Card onClose={ props.onClose }
      name={city.name}
      min={city.main.temp_min}
      max={city.main.temp_max} 
      img={city.weather[0].icon}/>
    </li>)

  return (
    <ul className={citiesStyle}>
      {cities}
    </ul>
  )
};

export default Cards;
